import * as fs from "fs";

const url = `https://rpc.helius.xyz/?api-key=6351fe34-1a98-45c2-bf82-3ed09b390676`;

const getAssetsByGroup = async () => {
  console.time("getAssetsByGroup"); // Start the timer
  let page = 1;
  const assetList = [];

  while (page > 0) {
    console.log("page: ", page);
    const response = await fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        jsonrpc: "2.0",
        id: "my-id",
        method: "getAssetsByGroup",
        params: {
          groupKey: "collection",
          groupValue: "J1S9H3QjnRtBbbuD4HjPV6RpRhwuk4zKbxsnCHuTgh9w",
          page: page,
          limit: 1000,
        },
      }),
    });
    const { result } = await response.json();

    assetList.push(...result.items);
    if (result.total !== 1000) {
      page = -1;
    } else {
      page++;
    }
  }

  const resultData = {
    results: assetList.map((item) => item.id),
  };
  fs.writeFileSync("mad-lads-assets.json", JSON.stringify(resultData));
  // console.log("Mad Lads Assets: ", resultData.totalResults);
};
getAssetsByGroup();
